mapper.select:
select e.codigo, e.nombre, e.asignaturas, e.versionPlanDeEstudio, e.numeroMatriculas, e.correo, e.programaAcademico,
s.numero as Solicitud_numero, s.fecha as Solicitud_fecha, s.justificacion as Solicitud_justificacion, s.impacto as Solicitud_impacto, s.proyeccion as Solicitud_proyeccion, s.comentarios as Solicitud_comentarios, s.estado as Solicitud_estado, s.acuseRecibo as Solicitud_acuseRecibo, s.avalConsejero as Solicitud_avalConsejero, s.necesitaAcuseRecibo as Solicitud_necesitaAcuseRecibo, s.asignatura_nemonico as Solicitud_asignatura_nemonico,
asi.nemonico as Solicitud_Asignatura_nemonico, asi.nombre as Solicitud_Asignatura_nombre FROM Estudiante as e left join Solicitud as s on s.Estudiante_codigo=e.codigo left join Asignatura as asi on asi.nemonico=s.asignatura_nemonico WHERE e.correo=#{correo}

select e.codigo, e.nombre, e.asignaturas, e.versionPlanDeEstudio, e.numeroMatriculas, e.correo, e.programaAcademico,
s.numero as Solicitud_numero, s.fecha as Solicitud_fecha, s.justificacion as Solicitud_justificacion, s.impacto as Solicitud_impacto, s.proyeccion as Solicitud_proyeccion, s.comentarios as Solicitud_comentarios, s.estado as Solicitud_estado, s.acuseRecibo as Solicitud_acuseRecibo, s.avalConsejero as Solicitud_avalConsejero, s.necesitaAcuseRecibo as Solicitud_necesitaAcuseRecibo, s.asignatura_nemonico as Solicitud_asignatura_nemonico,
asi.nemonico as Solicitud_Asignatura_nemonico, asi.nombre as Solicitud_Asignatura_nombre FROM Estudiante as e LEFT JOIN Solicitud as s on e.codigo=s.Estudiante_codigo LEFT JOIN Asignatura as asi on asi.nemonico=s.Asignatura_nemonico WHERE s.numero=#{codigo}

mapper.update:
update Estudiante set 
    asignaturas=#{asignaturas}            
WHERE codigo=#{codigo}
