mapper.update:
update Solicitud set
    estado=#{estado}
WHERE numero=#{numero}

update Solicitud set
    acuseRecibo=#{acuse}
WHERE numero=#{numero}

mapper.select:
select s.numero, s.fecha, s.justificacion, s.comentarios, s.estado, s.acuseRecibo, s.avalConsejero, s.necesitaAcuseRecibo, s.asignatura_nemonico,
asi.nemonico as Asignatura_nemonico, asi.nombre as Asignatura_nombre
FROM Estudiante as e LEFT JOIN CoordinadorCancelaciones as c on c.codigo=e.CoordinadorCancelaciones_codigo JOIN Solicitud as s on s.Estudiante_codigo=e.codigo LEFT JOIN Asignatura as asi on asi.nemonico=s.asignatura_nemonico WHERE c.codigo=#{codigo}
